	pIdTrabajador alias for $1;
	pCampo alias for $2;
	pIdGrupoNomina alias for $3;
	
	recTrabajador record;
	curTrabajador cursor (pIdTrabajador int4)
	for select id_trabajador from trabajadortemporal where id_trabajador = pIdTrabajador;
	
