    LANGUAGE plpgsql
    AS $_$
DECLARE
       feFecha ALIAS FOR $1;
       inAnios ALIAS FOR $2;
       feFechFin       DATE;
       sbIntervalo     INTERVAL;
       interval__units VARCHAR;
BEGIN
       interval__units := 'year';
       sbIntervalo := ('''' || inAnios || ' ' ||
interval__units || '''')::INTERVAL;
       SELECT feFecha + sbIntervalo  into feFechFin;
       RETURN feFechFin;
END;
$_$;


