				INNER JOIN trabajador AS t USING (id_trabajador)
			WHERE t.id_tipo_personal = pIdTipoPersonal
				AND dt.id_subtipo_dotacion = pIdSubtipoDotacion
				AND dt.anio = pAnio 
                                AND dt.mes  = pMes );
                        
		END IF;
	END IF;
        

