        ---
        IF pIdSubTipoDotacion = 0 THEN
           OPEN curTrabajadorTipo (pIdTipoPersonal, pIdTipoDotacion); 
		loop 		
       			fetch curTrabajadorTipo into recTrabajadorTipo;			 
        		exit when not found; 
