			OPEN curDotacionExiste (recTrabajadorTipo.id_trabajador,
					recTrabajadorTipo.id_subtipo_dotacion);

			FETCH curDotacionExiste INTO recDotacionExiste;			 
                        IF recDotacionExiste.id is null THEN
                           
				id_dotacion := id_dotacion + 1;
