						recTrabajadorSubtipo.id_subtipo_dotacion,
						vcantidad);
			ELSE
                           PERFORM update_dotacion_trabajador(recDotacionExiste.id,
                			vcantidad);
			END IF;
			close curDotacionExiste;
		end loop;
		close curTrabajadorSubtipo;
	END IF;
	return 0;
end;
$_$;


