	pTieneSemana5 alias for $11;
	pSemanaMes alias for $12;
	pUsuario alias for $13;
	pSemanaAnio alias for $14;
	pMesSemanal alias for $15;
	pPrimeraQuincena alias for $16;
	pSemanaQuincena alias for $17;
