							recConceptoFijoAsignacionEgresados.documento_soporte, 
							recConceptoFijoAsignacionEgresados.id_tipo_personal, pIdGrupoNomina,
							null, recConceptoFijoAsignacionEgresados.id_concepto_fijo,
							0, null);
					
		
