        aniosservicios              int4   :=0;
        anioanterior                int4   :=0;
        mesanterior                 int4   :=0;
        anio_fin                    int4   :=0;
        mes_fin                     int4   :=0;
        anio_ini                    int4   :=1997;
        mes_ini                     int4   :=6;
        anio_interes                int4   :=1997;
        mes_interes                 int4   :=6;
        dias_interes                float8 :=0;
