                              saldo_al_18061997,0);
              END IF;		
	        
        
        end loop;
        close curTrabajadores_1;

        raise notice 'PASA 10 ';

        perform liquidar_interes_adicional
                   (pIdTipoPersonal,pIdtrabajador,anioegreso,mesegreso);
       
        
        raise notice 'PASA 11 ';

        delete from identity;

	return 1;
end;
$_$;


