                           baseadicional :=recprestacionesmensualesadicional.monto_prestaciones
                                          + montoprestaciones;
                           montoadicional:= (baseadicional/60) * diasadicionales;
 
                        end if;
                        raise notice 'PASA 5 ';


                





                      if diasprestaciones = 5 then
                           raise notice 'PASA 6 ';
  
		           id := last_id_liquidacionprestacionesmensuales()+1;
		           if id is null then 
